import "../css/Section1.scss";
import Chili_loop from "../img/images/chili_loop.webm";
import Box_loop from "../img/images/box_loop.webm";
import Rosa_loop from "../img/images/rosa_loop.webm";
import BannerImg from "../img/section1/banner.png";
import { useMobileDetect } from "../hooks/mobile-detect.hook";

const CHARACTERS_SOURCE = [
  {
    src: Chili_loop,
    className: "left-girl",
  },
  {
    src: Box_loop,
    className: "center-man",
  },
  {
    src: Rosa_loop,
    className: "right-girl",
  },
];

function Section1() {
  const isMobile = useMobileDetect();

  return (
    <section className="section1">
      <div className="content">
        {/* list characters */}
        <div className="characters">
          {isMobile ? (
            <img className="banner-img" src={BannerImg} alt="banner"></img>
          ) : (
            <div className="video-wrapper">
              {CHARACTERS_SOURCE.map((item, index) => (
                <video
                  key={index}
                  className={`video-item ${item.className}`}
                  playsInline
                  autoPlay
                  muted
                  loop
                >
                  <source src={item.src} type="video/mp4" />
                  Your browser does not support HTML5 video.
                </video>
              ))}
            </div>
          )}
        </div>

        <div className="text-box">
          <p className="title">ALL DAY</p>

          <p className="sub-text">
            10+ Mini-games for you to play all day. Social Platform features
            integrated including Voice chat, relationship status, friend list,
            etc. Earning with your skills, for free!
          </p>

          <button className="button">JOIN NOW</button>

          <Icons />
        </div>
      </div>

      <div className="section-line"></div>
    </section>
  );
}

